<script>
export default {
  name: 'Form',
  data() {
    return {
      url: '',
    };
  },
  methods: {
    async send() {
      const body = {
        origin: this.url,
        email: 'grv1992@yandex.ru',
      };
      const data = await fetch('https://api.sitemap-generator.ru/task', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(body),
      });
      const resp = await data.json()
      this.$emit('sendedUrl', resp.id);
    }
  },
}
</script>

